import simulation 

# --- CONFIGURATION ---
DAILY_GENERATIONS = 20  
VIDEO_OUTPUT_DIR = "training_clips"
FPS = 30 
MAX_FRAMES_PRO = 1800      # 60s for the daily best
MAX_FRAMES_TRAINING = 450  # 15s for progress clips

if not os.path.exists(VIDEO_OUTPUT_DIR): os.makedirs(VIDEO_OUTPUT_DIR)

# Try to load theme (if generated by simulation.py logic)
try:
    with open("theme.json", "r") as f: THEME = json.load(f)
except:
    THEME = {"map_seed": 42}

